import React, { Component } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import NewTaskForm from './components/new-task-form';
import Footer from './components/footer';
import TaskList from './components/task-list';

class Main extends Component {
  maxId = 10;
  state = {
    todoData: [
      this.createTaskItem('coffe'),
      this.createTaskItem('coffes'),
      this.createTaskItem('coffesen'),
    ],
  };

  deleteItem = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);

      const newArr = [...todoData.slice(0, idx), ...todoData.slice(idx + 1)];
      return {
        todoData: newArr,
      };
    });
  };

  addItem = (text) => {
    const newItem = {
      label: text,
      editing: false,
      completed: false,
      id: this.maxId++,
    };

    this.setState(({ todoData }) => {
      const newArr = [...todoData, newItem];
    });
    return {
      todoData: newArr,
    };
  };

  createTaskItem(label) {
    return {
      label,
      editing: false,
      completed: false,
      id: this.maxId++,
    };
  }

  render() {
    return (
      <section className="main">
        <TaskList todos={this.state.todoData} onDeleted={this.deleteItem} />
        <Footer
          allItems={this.state.todoData}
          onToggleActive={this.onToggleActive}
          onToggleCompleted={this.onToggleCompleted}
        />
      </section>
    );
  }
}

const App = () => {
  return (
    <section className="todoapp">
      <NewTaskForm />
      <Main />
    </section>
  );
};

const root = ReactDOM.createRoot(document.querySelector('section'));
root.render(<App />);
