import React, { Component } from 'react';

export default class Task extends Component {
  constructor() {
    super();
    this.state = {
      completed: false,
      editing: false,
    };
  }
  onLableClick = () => {
    this.setState(({ completed }) => {
      return {
        completed: !completed,
      };
    });
  };

  onEditing = () => {
    this.setState({
      editing: true,
    });
  };
  onKeyDown = (e) => {
    if (e.key === 'Enter') {
      console.log(this.state.label);
      this.setState({
        editing: false,
      });
    }
  };

  onChange = (e) => {
    this.setState({
      label: e.target.value,
    });
  };

  render() {
    const { label, onDeleted, onToggleCompleted } = this.props;
    let classNames = '';
    const { completed, editing } = this.state;
    if (completed) {
      classNames = 'completed';
    }
    if (editing) {
      classNames = 'editing';
      return (
        <li className={classNames}>
          <div className="view">
            <input className="toggle" type="checkbox" />
            <label>
              <span className="description" onClick={this.onToggleCompleted}>
                {label}
              </span>
              <span className="created">created 5 minutes ago</span>
            </label>
            <button
              className="icon icon-edit"
              onClick={this.onEditing}
            ></button>
            <button className="icon icon-destroy" onClick={onDeleted}></button>
          </div>
          <input
            type="text"
            className="edit"
            defaultValue={label}
            onKeyDown={this.onKeyDown}
            onChange={this.onChange}
          />
        </li>
      );
    }
    return (
      <li className={classNames}>
        <div className="view">
          <input className="toggle" type="checkbox" />
          <label>
            <span className="description" onClick={this.onToggleCompleted}>
              {label}
            </span>
            <span className="created">created 5 minutes ago</span>
          </label>
          <button className="icon icon-edit" onClick={this.onEditing}></button>
          <button className="icon icon-destroy" onClick={onDeleted}></button>
        </div>
      </li>
    );
  }
}
