import React, { Component } from 'react';

export default class Task extends Component {
  onEditing = () => {
    this.setState({
      editing: true,
    });
  };

  onChange = (e) => {
    this.setState({
      label: e.target.value,
    });
  };

  render() {
    const {
      label,
      onDeleted,
      onToggleCompleted,
      onToggleEditing,
      completed,
      editing,
    } = this.props;
    let classNames = '';
    if (completed) {
      classNames = 'completed';
    }
    if (editing) {
      classNames = 'editing';
      return (
        <li className={classNames}>
          <div className="view">
            <input className="toggle" type="checkbox" />
            <label>
              <span className="description" onClick={onToggleCompleted}>
                {label}
              </span>
              <span className="created">created 5 minutes ago</span>
            </label>
            <button
              className="icon icon-edit"
              onClick={this.onEditing}
            ></button>
            <button className="icon icon-destroy" onClick={onDeleted}></button>
          </div>
          <input
            type="text"
            className="edit"
            defaultValue={label}
            onKeyDown={(e) => (e.key === 'Enter' ? onToggleEditing : 'd')}
            onChange={this.onChange}
          />
        </li>
      );
    }
    return (
      <li className={classNames}>
        <div className="view">
          <input className="toggle" type="checkbox" />
          <label>
            <span className="description" onClick={onToggleCompleted}>
              {label}
            </span>
            <span className="created">created 5 minutes ago</span>
          </label>
          <button className="icon icon-edit" onClick={this.onEditing}></button>
          <button className="icon icon-destroy" onClick={onDeleted}></button>
        </div>
      </li>
    );
  }
}
