import React, { Component } from 'react'

import NewTaskForm from '../new-task-form'
import Footer from '../footer'
import TaskList from '../task-list'

export default class App extends Component {
  maxId = 1
  state = {
    todoData: [
      this.createTaskItem('coffe', '10', '11'),
      this.createTaskItem('coffes'),
      this.createTaskItem('coffesen'),
    ],
    statusFilter: 'All',
    play: true,
    active: false,
  }
  static defaultProps = {
    min: '00',
    sec: '00',
  }
  componentDidMount() {
    this.setState({ min: this.props.min, sec: this.props.sec })
  }
  componentDidUpdate(prevProps) {
    if (this.state.min == 0 && this.state.sec == 0 && !this.props.completed) {
      this.setState({ play: false })
      if (!this.props.completed) {
        this.props.onToggleCompleted()
      }
    }

    if (this.state.sec !== prevProps.sec) {
      this.props.updateItem(this.props.id, this.state.min, this.state.sec)
    }
  }

  timerS = (props) => {
    if (props <= 10 && props > 0) this.setState({ sec: `0${props - 1}` })
    else if (props > 0) this.setState({ sec: String(props - 1) })
  }
  timerM = (props) => {
    if (props <= 10 && props > 0) this.setState({ min: `0${props - 1}`, sec: 59 })
    else if (props > 0) this.setState({ min: String(props - 1), sec: '59' })
  }
  timer = () => {
    if (!this.state.active && this.state.play) {
      setInterval(() => {
        if (this.state.play && this.state.sec > 0) {
          this.timerS(this.state.sec)
        } else if (this.state.play && this.state.sec == 0 && this.state.min > 0) {
          this.timerM(this.state.min)
          this.setState({ sec: '59' })
        }
      }, 1000)
    }
  }

  deleteItem = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id)

      const newArr = [...todoData.slice(0, idx), ...todoData.slice(idx + 1)]
      return {
        todoData: newArr,
      }
    })
  }
  updateItem = (id, min, sec) => {
    this.setState(({ todoData }) => {
      const newArr = [...todoData].map((item) => {
        if (item.id === id) {
          item.sec = sec
          item.min = min
        }
        return item
      })
      return {
        todoData: newArr,
      }
    })
  }
  saveItem = (id, text) => {
    this.setState(({ todoData }) => {
      const newArr = [...todoData].map((item) => {
        if (item.id === id) {
          item.label = text
          item.editing = false
        }
        return item
      })
      return {
        todoData: newArr,
      }
    })
  }

  addItem = (text, min, sec) => {
    const newItem = this.createTaskItem(text, min, sec)

    this.setState(({ todoData }) => {
      const newArr = [...todoData, newItem]
      return {
        todoData: newArr,
      }
    })
  }

  createTaskItem(label, min, sec) {
    return {
      label,
      editing: false,
      completed: false,
      checked: false,
      id: this.maxId++,
      date: new Date(),
      min,
      sec,
    }
  }

  onToggleCompleted = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id)
      const oldItem = todoData[idx]
      const newItem = {
        ...oldItem,
        completed: !oldItem.completed,
      }
      const newArr = [...todoData.slice(0, idx), newItem, ...todoData.slice(idx + 1)]
      return {
        todoData: newArr,
      }
    })
  }
  onToggleEditing = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id)
      const oldItem = todoData[idx]
      const newItem = { ...oldItem, editing: !oldItem.editing }
      const newArr = [...todoData.slice(0, idx), newItem, ...todoData.slice(idx + 1)]
      return {
        todoData: newArr,
      }
    })
  }
  clearCompleted = () => {
    this.setState(({ todoData }) => {
      const completed = todoData.filter((item) => {
        return !item.completed
      })
      return {
        todoData: completed,
      }
    })
  }

  filterChange = (status) => {
    this.setState({
      statusFilter: status,
    })
  }

  filterTask(arr, status) {
    if (status !== 'All') {
      let i
      const newArr = arr.filter((item) => {
        if (status === 'Active') {
          console.log(this.state.noCompletedTimer)
          clearInterval(i)
          return !item.completed
        }
        if (!item.completed && this.state.activeTimer === false) {
          i = setInterval(() => {
            this.setState((state) => {
              console.log(state)

              return { noCompletedTimer: state.noCompletedTimer + 1, activeTimer: true }
            })
          }, 1000)
        }

        return item.completed
      })
      return newArr
    }
    return arr
  }

  render() {
    const active = this.state.todoData.filter((item) => !item.completed && !item.editing)
    const { todoData, statusFilter } = this.state
    const renderTodo = this.filterTask(todoData, statusFilter)
    return (
      <section className="todoapp">
        <NewTaskForm onItemAdded={this.addItem} />
        <section className="main">
          <TaskList
            todos={renderTodo}
            updateItem={this.updateItem}
            onDeleted={this.deleteItem}
            onChange={this.onChange}
            saveItem={this.saveItem}
            onToggleCompleted={this.onToggleCompleted}
            onToggleEditing={this.onToggleEditing}
            timer={this.timer}
            playFunction={this.playFunction}
          />
          <Footer
            filterChange={this.filterChange}
            clearCompleted={this.clearCompleted}
            active={active.length}
            statusFilter={statusFilter}
          />
        </section>
      </section>
    )
  }
}
