import React, { Component } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import NewTaskForm from './components/new-task-form';
import Footer from './components/footer';
import TaskList from './components/task-list';

class App extends Component {
  maxId = 10;
  state = {
    todoData: [
      this.createTaskItem('coffe'),
      this.createTaskItem('coffes'),
      this.createTaskItem('coffesen'),
    ],
  };

  deleteItem = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);

      const newArr = [...todoData.slice(0, idx), ...todoData.slice(idx + 1)];
      return {
        todoData: newArr,
      };
    });
  };

  saveItem = (id, text) => {
    this.setState(({ todoData }) => {
      const newArr = [...todoData].map((item) => {
        if (item.id === id) {
          item.label = text;
        }
        return item;
      });
      return {
        todoData: newArr,
      };
    });
  };

  addItem = (text) => {
    const newItem = this.createTaskItem(text);

    this.setState(({ todoData }) => {
      const newArr = [...todoData, newItem];
      return {
        todoData: newArr,
      };
    });
  };

  createTaskItem(label) {
    return {
      label,
      editing: false,
      completed: false,
      id: this.maxId++,
    };
  }

  onToggleCompleted = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);
      const oldItem = todoData[idx];
      const newItem = { ...oldItem, completed: !oldItem.completed };
      const newArr = [
        ...todoData.slice(0, idx),
        newItem,
        ...todoData.slice(idx + 1),
      ];
      return {
        todoData: newArr,
      };
    });
  };
  onToggleEditing = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);
      const oldItem = todoData[idx];
      const newItem = { ...oldItem, editing: !oldItem.editing };
      const newArr = [
        ...todoData.slice(0, idx),
        newItem,
        ...todoData.slice(idx + 1),
      ];
      return {
        todoData: newArr,
      };
    });
  };
  clearCompleted = () => {
    this.setState(({ todoData }) => {
      const completed = todoData.filter((item) => {
        return !item.completed;
      });
      return {
        todoData: completed,
      };
    });
  };

  filterCompleted = () => {
    this.setState(({ todoData }) => {
      const completed = todoData.filter((item) => {
        return item.completed;
      });

      return {
        todoData: completed,
      };
    });
  };
  filterActive = () => {
    this.setState(({ todoData }) => {
      const active = todoData.filter((item) => {
        return !item.completed && !item.editing;
      });
      return {
        todoData: active,
      };
    });
  };
  filterAll = () => {
    this.setState(({ todoData }) => {
      console.log('aaa');
      return {
        todoData,
      };
    });
  };

  render() {
    const completed = this.state.todoData.filter((item) => item.completed);
    const active = this.state.todoData.filter(
      (item) => !item.completed && !item.editing
    );
    return (
      <section className="todoapp">
        <NewTaskForm onItemAdded={this.addItem} />
        <section className="main">
          <TaskList
            todos={this.state.todoData}
            onDeleted={this.deleteItem}
            onChange={this.onChange}
            saveItem={this.saveItem}
            onToggleCompleted={this.onToggleCompleted}
            onToggleEditing={this.onToggleEditing}
          />
          <Footer
            filterCompleted={this.filterCompleted}
            filterActive={this.filterActive}
            filterAll={this.filterAll}
            clearCompleted={this.clearCompleted}
            active={active}
            completed={completed}
          />
        </section>
      </section>
    );
  }
}

const root = ReactDOM.createRoot(document.querySelector('section'));
root.render(<App />);
