import React, { Component } from 'react'
import './new-task-form.css'
export default class NewTaskForm extends Component {
  state = {
    label: '',
    min: '',
    max: '',
  }
  onLableChange = (e) => {
    this.setState({
      label: e.target.value,
    })
  }
  onMinChange = (e) => {
    this.setState({
      min: e.target.value,
    })
  }
  onMaxChange = (e) => {
    this.setState({
      max: e.target.value,
    })
  }

  onKeyDown = (e) => {
    if (e.key === 'Enter') {
      this.props.onItemAdded(this.state.label)
      this.setState({
        label: '',
      })
    }
  }
  render() {
    return (
      <header className="header">
        <h1>todos</h1>
        <form className="new-todo-form">
          <input
            className="new-todo"
            onChange={this.onLableChange}
            onKeyDown={this.onKeyDown}
            placeholder="What needs to be done?"
            value={this.state.label}
          ></input>
          <input onChange={this.onMinChange} className="new-todo-form__timer" placeholder="Min" />
          <input onChange={this.onMaxChange} className="new-todo-form__timer" placeholder="Max" />
        </form>
      </header>
    )
  }
}
