import React, { Component } from 'react'
import { func, string, number, instanceOf, bool } from 'prop-types'
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

export default class Task extends Component {
  state = {
    label: '',
    min: '0',
  }
  static defaultProps = {
    completed: false,
    editing: false,
    min: '0',
    sec: '0',
  }

  static propTypes = {
    id: number.isRequired,
    label: string.isRequired,
    min: string.isRequired,
    sec: string.isRequired,
    completed: bool,
    editing: bool,
    date: instanceOf(Date),
    onToggleEditing: func.isRequired,
    onDeleted: func.isRequired,
    onToggleCompleted: func.isRequired,
    saveItem: func.isRequired,
  }
  componentDidMount() {
    this.setState({ min: this.props.min, sec: this.props.sec })
  }
  componentDidUpdate(prevProps) {
    setTimeout(() => {
      console.log(this.props.min)
      console.log(prevProps.min)
      this.timer(this.props.min - 1)
    }, 1000)
  }

  onChange = (e) => {
    this.setState({
      label: e.target.value,
    })
  }
  timer = (props) => {
    this.setState({ min: props })
  }
  render() {
    const { id, label, date, onDeleted, onToggleCompleted, onToggleEditing, completed, editing, saveItem } = this.props
    const { sec } = this.state
    let classNames = ''
    if (completed) {
      classNames = 'completed'
    }
    if (editing) {
      classNames = 'editing'
    }
    const Timer = ({ min, sec }) => {
      return `${min}:${sec}`
    }
    return (
      <li className={classNames}>
        <div className="view">
          <input className="toggle" type="checkbox" onChange={onToggleCompleted} checked={completed} />
          <label>
            <span className="title">{label}</span>
            <span className="description">
              <button className="icon icon-play"></button>
              <button className="icon icon-pause"></button>
              <Timer min={this.state.min} sec={sec} />
            </span>
            <span className="description">
              created{' '}
              {formatDistanceToNow(date, {
                includeSeconds: true,
                addSuffix: true,
              })}
            </span>
          </label>
          <button className="icon icon-edit" onClick={onToggleEditing}></button>
          <button className="icon icon-destroy" onClick={onDeleted}></button>
        </div>
        {editing ? (
          <input
            type="text"
            className="edit"
            defaultValue={label}
            onChange={this.onChange}
            onKeyDown={(e) => {
              if (e.key === 'Enter') {
                this.state.label.length > 0 ? saveItem(id, this.state.label) : saveItem(id, label)
              }
            }}
          />
        ) : (
          false
        )}
      </li>
    )
  }
}
